import React from 'react';
//import Router from 'next/router';
import Link from './Link';

export default MainAdv => (
  <div className="container-fluid mainAdv">
    <div className="dots-right" />
    <div className="dots-left" />
    <div className="gaz-mash" />
    <div className="border-left" />
    <div className="border-right" />

    <div className="container main-container">
      <div className="row">
        <div className="col-12 col-md-6">
          <div className="text-container">
            <span className="main-heading">
              Газопоршневые электростанции под ключ
            </span>
            <div className="spartners">
              <div className="logos-container"></div>
              <span className="descr-min">
                мы являемся официальными сервисными партнёрами
              </span>
            </div>
            <h1 className="main-desc">
              Проектирование, производство и обслуживание газопоршневых
              электростанций и мини ТЭЦ.
            </h1>
          </div>
        </div>
        <div className="col-12 col-md-6">
          <div className="promo-fon" />
          <div className="img-container">
            <img
              src="/images/about-2.jpg"
              alt="Фото объекта"
              title="Энергоцентр ЯМЗ"
              className="img-main"
            />
          </div>
        </div>
      </div>
    </div>
    <style jsx>{`
      .mainAdv {
        overflow: hidden;
        background: linear-gradient(to right, #e1e1e1 0%, #b1b1b1 100%);
        display: block;
        min-height: 500px;
        z-index: 0;
        position: relative;
      }
      .mainAdv .dots-right {
        position: absolute;
        display: block;
        width: 213px;
        height: 259px;
        top: 0;
        right: 0;
        background: url('/images/dots-min.png') 100% -50% no-repeat;
      }
      .mainAdv .dots-left {
        position: absolute;
        display: block;
        width: 213px;
        height: 259px;
        left: 0;
        bottom: 0;
        background: url('/images/dots-min.png') 0% 120% no-repeat;
      }
      .mainAdv .gaz-mash {
        position: absolute;
        display: block;
        z-index: -1;
        left: 0;
        right: 0;
        top: 30px;
        opacity: 0.3;
        width: auto;
        height: 150px;
        background: url('/images/bg-gaz-mash.png') 0% 0% no-repeat;
        background-size: contain;
      }
      .mainAdv .border-left {
        display: block;
        position: absolute;
        z-index: -1;
        top: 50%;
        left: -35px;
        width: 50px;
        height: 285px;
        transform: translateY(-50%);
        border: 5px solid rgba(255, 255, 255, 0.5);
        border-radius: 3px;
      }
      .mainAdv .border-right {
        display: block;
        position: absolute;
        z-index: -1;
        top: 50%;
        right: -35px;
        width: 50px;
        height: 285px;
        transform: translateY(-50%);
        border: 5px solid rgba(255, 255, 255, 0.5);
        border-radius: 3px;
      }
      .mainAdv .main-container {
        display: flex;
        align-items: center;
        justify-content: center;
        min-height: 100%;
        height: 500px;
      }
      .mainAdv .main-container .row .col-12 .text-container .main-heading {
        display: block;
        font-family: var(--molot-font);
        font-size: 28px;
        line-height: 32px;
        text-shadow: 3px 3px 2px #8f8f8f;
        color: white;
        letter-spacing: 0.5px;
        margin-bottom: 15px;
      }
      .mainAdv .main-container .row .col-12 .text-container .spartners {
        margin-bottom: 20px;
      }
      .mainAdv
        .main-container
        .row
        .col-12
        .text-container
        .spartners
        .logos-container {
        width: 100%;
        height: 30px;
        margin: 0;
        background: url('/images/liebherr.png') 0 50% no-repeat;
        background-size: contain;
      }
      .mainAdv
        .main-container
        .row
        .col-12
        .text-container
        .spartners
        .descr-min {
        display: block;
        margin-top: 3px;
        font-size: 10px;
        line-height: 10px;
        font-weight: 300;
      }
      .mainAdv .main-container .row .col-12 .text-container .main-desc {
        font-size: 16px;
        font-weight: 500;
        color: #383838;
      }
      .mainAdv .main-container .row .col-12 .img-container {
        display: flex;
        flex-direction: row-reverse;
      }
      .mainAdv .main-container .row .col-12 .img-container .img-main {
        border: 5px solid white;
        margin-top: 15px;
        display: block;
        height: 130px;
        width: auto;
      }

      @media (min-width: 768px) {
        .mainAdv {
          overflow: hidden;
          display: block;
          min-height: 500px;
          z-index: 0;
          position: relative;
        }
        .mainAdv .dots-right {
          position: absolute;
          display: block;
          width: 213px;
          height: 259px;
          top: 0;
          right: 0;
          background: url('/images/dots-min.png') 100% 0% no-repeat;
        }
        .mainAdv .dots-left {
          position: absolute;
          display: block;
          width: 213px;
          height: 259px;
          left: 0;
          bottom: 0;
          background: url('/images/dots-min.png') 0% 100% no-repeat;
        }
        .mainAdv .gaz-mash {
          right: 0;
          top: 30px;
          width: auto;
          height: 150px;
          background: url('/images/bg-gaz-mash.png') 0% 0% no-repeat;
          background-size: contain;
        }
        .mainAdv .border-left {
          display: block;
          position: absolute;

