sourcesContent:
import React, { Component, useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import TextFieldGroup from '../common/TextFieldGroup';
import RadioInputGroup from '../common/RadioInputGroup';
// import { connect } from 'react-redux';
// import { validateFormData } from '../../actions/formActions';

import axios from 'axios';
import validateFormData from '../validation/mailer';

function AskForm(props) {
  const [info, setInfo] = useState({
    organizationname: '',
    organizationadres: '',
    personname: '',
    personstatus: '',
    personemail: '',
    personphone: '',
    personmobile: '',
    locationparams: '',
    projectmountadres: '',
    projectdescription: '',
    operationmode: '',
    proposedmodel: '',
    electricpower: '',
    connectiontype: '',
    minimalload: '',
    energyloadgraph: '',
    installationsettings: '',
    fueltype: '',
    utilsystem: '',
    reqheatpower: '',
    chefmontage: '',
    stafflearning: '',
    otherworks: '',
    gasprice: '',
    currentenergytarif: '',
    currentheattarif: '',
    commentary: '',
    isValid: false,
    errors: {},
  });

  function onChange(e) {
    console.log(e.target.name);

    setInfo({ [e.target.name]: e.target.value });
  }

  function onSelect(e) {
    setInfo({ [e.target.name]: e.target.value });
  }

  function handleSubmit(event) {
    event.preventDefault();

    const askFormData = {
      organizationname: info.organizationname,
      organizationadres: info.organizationadres,
      personname: info.personname,
      personstatus: info.personstatus,
      personemail: info.personemail,
      personphone: info.personphone,
      personmobile: info.personmobile,
      locationparams: info.locationparams,
      projectmountadres: info.projectmountadres,
      projectdescription: info.projectdescription,
      operationmode: info.operationmode,
      proposedmodel: info.proposedmodel,
      electricpower: info.electricpower,
      connectiontype: info.connectiontype,
      minimalload: info.minimalload,
      energyloadgraph: info.energyloadgraph,
      installationsettings: info.installationsettings,
      fueltype: info.fueltype,
      utilsystem: info.utilsystem,
      reqheatpower: info.reqheatpower,
      chefmontage: info.chefmontage,
      stafflearning: info.stafflearning,
      otherworks: info.otherworks,
      gasprice: info.gasprice,
      currentenergytarif: info.currentenergytarif,
      currentheattarif: info.currentheattarif,
      commentary: info.commentary,
    };

    setInfo(validateFormData(askFormData));
    console.log(info.isValid);
    // this.state.isValid
    //   ? axios
    //       .post('/api/form-validation', askFormData)
    //       .then((res) => {
    //         console.log(res);
    //       })
    //       .catch(function (error) {
    //         console.log(error);
    //       })
    //   : '';
    window.scrollTo(0, 0);
  }
  return (
    <form onSubmit={() => handleSubmit}>
      <div className="container-fluid form-razdel ask-form">
        <div className="container">
          <div className="row">
            <div className="col">
              <h1 className="header-min-colored mb-2">
                Опросный лист для расчёта параметров проекта
              </h1>
              {/* <small className="d-block">* = обязательные поля</small> */}
            </div>
          </div>
        </div>
      </div>
      <div className="container-fluid form-razdel razdel-contacts">
        <div className="container">
          <div className="row">
            <div className="col">
              <div className="razdel">
                <span className="razdel-title">
                  1. Контактные данные организации и лица, заполняющего опросный
                  лист
                </span>
                <div className="fields">
                  <TextFieldGroup
                    name="organizationname"
                    placeholder="ООО 'Ваша организация'"
                    label="Название организации:"
                    value={info.organizationname}
                    onChange={onChange}
                    error={info.errors?.organizationname}
                    info=""
                  />
                  <TextFieldGroup
                    name="organizationadres"
                    placeholder="123456 г. Ярославль, дом, корпус, помещение..."
                    label="Адрес организации:"
                    value={info.organizationadres}
                    onChange={onChange}
                    error={info.errors?.organizationadres}
                  />
                  <TextFieldGroup
                    name="personname"
                    placeholder="Иванов Иван Иванович"
                    label="ФИО:"
                    value={info.personname}
                    onChange={onChange}
                    error={info.errors?.personname}
                  />
                  <TextFieldGroup
                    name="personstatus"
                    placeholder="Коммерческий директор"
                    label="Должность:"
                    value={info.personstatus}
                    onChange={onChange}
                    error={info.errors?.personstatus}
                  />
                  <TextFieldGroup
                    name="personemail"
                    placeholder="mail@mail.ru"
                    label="e-mail:"
                    value={info.personemail}
                    onChange={onChange}
                    error={info.errors?.personemail}
                  />
                  <TextFieldGroup
                    name="personphone"
                    placeholder="8-4852-12-34-56"
                    label="Городской телефон:"
                    value={info.personphone}
                    onChange={onChange}
                    error={info.errors?.personphone}
                  />
                  <TextFieldGroup
                    name="personmobile"
                    placeholder="8-903-123-45-67"
                    label="Мобильный телефон:"
                    value={info.personmobile}
                    onChange={onChange}
                    error={info.errors?.personmobile}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="container-fluid form-razdel project-description">
        <div className="container">
          <div className="row">
            <div className="col">
              <div className="razdel">
                <h2 className="razdel-title">
                  2. Характеристика места установки оборудования
                </h2>
                <div className="fields">
                  <TextFieldGroup
                    name="locationparams"
                    placeholder="промышленное предприятие, жилая зона, др."
                    label="Характеристики места расположения:"
                    value={info.locationparams}
                    onChange={onChange}
                    error={info.errors?.locationparams}
                  />
                  <TextFieldGroup
                    name="projectmountadres"
                    placeholder="адрес установки"
                    label="Адрес установки (город расположения монтажной площадки): "
                    value={info.projectmountadres}
                    onChange={onChange}
                    error={info.errors?.projectmountadres}
                  />
                  <TextFieldGroup
                    name="projectdescription"
                    placeholder="наименование объекта, описание "
                    label="Дополнительная информация о проекте: "
                    value={info.projectdescription}
                    onChange={onChange}
                    error={info.errors?.projectdescription}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="container-fluid form-razdel parametres">
        <div className="container">
          <div className="row">
            <div className="col">
              <div className="razdel">
                <h2 className="razdel-title">
                  3. Основные параметры для выбора оборудования
                </h2>
                <div className="fields">
                  <RadioInputGroup
                    name="operationmode"
                    label="Режим работы оборудования: "
                    values={[
                      'Резервный генератор (аварийный)',
                      'Посменный длительный режим',
                      'Постоянный режим работы (круглосуточный)',
                    ]}
                    askother={true}
                    otherlabel="Другой"
                    onSelect={onSelect}
                    error={info.errors?.operationmode}
                  />
                  <TextFieldGroup
                    name="proposedmodel"
                    placeholder="например, «2  по 400 кВт»: "
                    label="Предполагаемая модель (единичная электрическая мощность) 
                    и количество генераторных установок: "
                    value={info.proposedmodel}
                    onChange={onChange}
                    error={info.errors?.proposedmodel}
                  />
                  {/* <span>
                      Электрические параметры для подбора оборудования:
                    </span> */}
                  <TextFieldGroup
                    name="electricpower"
                    placeholder="Требуемая электрическая мощность (кВт): "
                    label="Требуемая электрическая мощность (кВт): "
                    value={info.electricpower}
                    onChange={onChange}
                    error={info.errors?.electricpower}
                  />
                  <RadioInputGroup
                    name="connectiontype"
                    label="Вид подключения: "
                    values={[
                      'Один генератор на изолированную нагрузку (изолированно от централизованной сети)',
                      'Параллельная работа нескольких генераторов на общую нагрузку (изолированно от сети )',
                      'Параллельная работа с централизованной сетью (с запретом экспорта электроэнергии в сеть)',
                    ]}
                    askother={true}
                    otherlabel="Другое"
                    onSelect={onSelect}
                    error={info.errors?.connectiontype}
                  />
                  <TextFieldGroup
                    name="minimalload"
                    placeholder="кВт"
                    label="Минимальная продолжительная нагрузка: "
                    value={info.minimalload}
                    onChange={onChange}
                    error={info.errors?.minimalload}
                  />
                  <RadioInputGroup
                    name="installationsettings"
                    label="Варианты исполнения и размещения генераторных установок: "
                    values={[
                      'Поставка в открытом исполнении (монтаж в здании)',
                      'Поставка в шумозащитном кожухе на улице',
                      'Поставка в шумозащитном кожухе в здании',
                      'В контейнере (монтажная площадка расположена на улице)',
                    ]}
                    askother={true}
                    otherlabel="Другое"
                    onSelect={onSelect}
                    error={info.errors?.installationsettings}
                  />
                  <RadioInputGroup
                    name="fueltype"
                    label="Тип и характеристики используемого топлива: "
                    values={['Магистральный природный газ (метан)']}
                    askother={true}
                    otherlabel="Другой (попутный газ, сжиженный пропан-бутан, биогаз, др.) 
											(для любого газа кроме метана, приложить компонентный состав)
											"
                    onSelect={onSelect}
                    error={info.errors?.fueltype}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="container-fluid form-razdel heat-params">
        <div className="container">
          <div className="row">
            <div className="col">
              <div className="razdel">
                <h2 className="razdel-title">4. Тепловые параметры</h2>
                <div className="fields">
                  <TextFieldGroup
                    name="utilsystem"
                    placeholder="да / нет"
                    label="Система Утилизации Тепла (СУТ): "
                    value={info.utilsystem}
                    onChange={onChange}
                    error={info.errors?.utilsystem}
                  />
                  <TextFieldGroup
                    name="reqheatpower"
                    placeholder="кВт"
                    label="Требуемая тепловая мощность: "
                    value={info.reqheatpower}
                    onChange={onChange}
                    error={info.errors?.reqheatpower}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="container-fluid form-razdel add-works">
        <div className="container">
          <div className="row">
            <div className="col">
              <div className="razdel">
                <h2 className="razdel-title">5. Дополнительные виды работ</h2>
                <div className="fields">
                  <TextFieldGroup
                    name="chefmontage"
                    placeholder="Шеф-монтажные работы и Пуско-наладочные работы: "
                    label="Шеф-монтажные работы и Пуско-наладочные работы: "
                    value={info.chefmontage}
                    onChange={onChange}
                    error={info.errors?.chefmontage}
                  />
                  <TextFieldGroup
                    name="stafflearning"
                    placeholder="Обучение эксплуатирующего персонала: "
                    label="Обучение эксплуатирующего персонала: "
                    value={info.stafflearning}
                    onChange={onChange}
                    error={info.errors?.stafflearning}
                  />
                  <TextFieldGroup
                    name="otherworks"
                    placeholder="Перечислите желаемые дополнительные работы и услуги: "
                    label="Перечислите желаемые дополнительные работы и услуги: "
                    value={info.otherworks}
                    onChange={onChange}
                    error={info.errors?.otherworks}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="container-fluid form-razdel economic-params">
        <div className="container">
          <div className="row">
            <div className="col">
              <div className="razdel">
                <h2 className="razdel-title">6. Экономические параметры</h2>
                <span className="title-description">
                  (необходимо для проведения технико-экономического обоснования)
                </span>
                <div className="fields">
                  <TextFieldGroup
                    name="gasprice"
                    placeholder="Стоимость 1000 м3 газа (руб./1000 м3): "
                    label="Стоимость 1000 м3 газа (руб./1000 м3): "
                    value={info.gasprice}
                    onChange={onChange}
                    error={info.errors?.gasprice}
                  />
                  <TextFieldGroup
                    name="currentenergytarif"
                    placeholder="Текущий тариф на электроэнергию, руб./кВт*час: "
                    label="Текущий тариф на электроэнергию, руб./кВт*час: "
                    value={info.currentenergytarif}
                    onChange={onChange}
                    error={info.errors?.currentenergytarif}
                  />
                  <TextFieldGroup
                    name="currentheattarif"
                    placeholder="Текущий тариф на тепловую энергию, руб./Гкал: "
                    label="Текущий тариф на тепловую энергию, руб./Гкал: "
                    value={info.currentheattarif}
                    onChange={onChange}
                    error={info.errors?.currentheattarif}
                  />
                  <TextFieldGroup
                    name="commentary"
                    placeholder="Комментарии: "
                    label="Комментарии: "
                    value={info.commentary}
                    onChange={onChange}
                    error={info.errors?.commentary}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="container-fluid form-razdel button-section pb-5">
        <div className="container">
          <div className="row">
            <div className="col">
              <div className="button-container">
                <button className="button-submit" type="submit">
                  Отправить данные
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <style jsx global>{`
        .razdel-title {
          display: block;
          text-transform: uppercase;
          font-size: 17px;
          font-weight: 900;
          color: var(--accent-grey);
          line-height: 23px;
          margin-bottom: 0px;
        }
        .title-description {
          font-size: 14px;
          line-height: 12px;
        }
        .form-razdel {
          padding-top: 30px;
          padding-bottom: 15px;
        }
        .fields {
          padding-top: 15px;
          padding-bottom: 15px;
        }
        .fields .label-title {
          display: block;
          font-size: 16px;
          line-height: 20px;
          font-weight: 700;
          margin-top: 7px;
          margin-bottom: 5px;
        }
        .fields .form-control {
          font-size: 16px;
          padding-left: 10px;
          font-weight: 300;
        }
        .fields .radio-input-group .label-title {
          margin-bottom: 10px;
        }
        .fields .radio-input-group .radio-input-field {
          display: flex;
          align-items: center;
          margin-bottom: 8px;
          padding-left: 25px;
        }
        .fields .radio-input-group .radio-input-field input {
          width: 100%;
          height: inherit;
          position: absolute;
          left: 0;
          z-index: 1;
          opacity: 0;
          margin-right: 10px;
        }
        .fields
          .radio-input-group
          .radio-input-field
          input:checked
          + label::after {
          opacity: 1;
        }
        .fields .radio-input-group .radio-input-field label {
          position: relative;
          font-size: 14px;
          line-height: 16px;
          margin-bottom: 0;
          margin-left: 5px;
          font-weight: 300;
          cursor: pointer;
        }
        .fields .radio-input-group .radio-input-field label::before {
          position: absolute;
          display: block;
          content: '';
          top: 50%;
          transform: translateY(-50%);
          left: -30px;
          width: 20px;
          height: 20px;
          border-radius: 13px;
          border: 1px solid rgba(0, 0, 0, 0.4);
        }
        .fields .radio-input-group .radio-input-field label::after {
          position: absolute;
          display: block;
          content: '';
          top: 50%;
          transform: translateY(-50%);
          left: -25px;
          width: 10px;
          height: 10px;
          border-radius: 13px;
          background: var(--accent-red);
          opacity: 0;
        }
        .radio:checked + label:after {
          opacity: 1;
        }
        .radio:focus + label:before {
          box-shadow: 0 0 0 3px rgba(255, 255, 0, 0.7);
        }
        .fields .radio-input-group .radio-input-field:last-child {
          display: block;
        }
        .form-razdel:nth-child(even) {
          background-color: #fff;
        }

        .button-section .button-submit {
          display: block;
          margin: 0 auto;
          display: block;
          border-radius: 50px;
          color: #fff;
          font-family: var(--molot-font);
          background-color: var(--accent-red);
          border: none;
          padding: 12px 20px;
          letter-spacing: 1px;
          margin: 15px auto;
          box-shadow: var(--box-shadow-up);
          font-size: 15px;
          transition: all 0.2s ease-in;
        }
        .button-section .button-submit:hover {
          cursor: pointer;
          box-shadow: var(--box-shadow);
        }
        @media (min-width: 768px) {
          .razdel-title {
            display: block;
            text-transform: uppercase;
            font-size: 21px;
            font-weight: 300;
            color: var(--accent-grey);
            line-height: 28px;
            margin-bottom: 0px;
          }
          .title-description {
            font-size: 16px;
            line-height: 12px;
          }
          .form-razdel {
            padding-top: 30px;
            padding-bottom: 15px;
          }
          .fields {
            padding-top: 15px;
            padding-bottom: 15px;
          }
          .fields .label-title {
            display: block;
            font-size: 20px;
            line-height: 24px;
            font-weight: 700;
            margin-top: 7px;
            margin-bottom: 10px;
          }
          .fields .form-control {
            font-size: 16px;
            padding-left: 10px;
          }
          .fields .radio-input-group .label-title {
            margin-bottom: 12px;
            font-size: 20px;
            line-height: 24px;
          }
          .fields .radio-input-group .radio-input-field {
            display: flex;
            align-items: center;
            margin-bottom: 12px;
          }
          .fields .radio-input-group .radio-input-field input {
            margin-right: 10px;
          }
          .fields .radio-input-group .radio-input-field label {
            font-size: 16px;
            line-height: 18px;
            margin-bottom: 0;
            font-weight: 300;
          }
        }

        @media (min-width: 1200px) {
          .razdel-title {
            display: block;
            text-transform: uppercase;
            font-size: 27px;
            font-weight: 300;
            color: var(--accent-grey);
            line-height: 34px;
            margin-bottom: 0px;
            padding-right: 300px;
          }
          .title-description {
            font-size: 16px;
            line-height: 12px;
          }
          .form-razdel {
            padding-top: 30px;
            padding-bottom: 15px;
          }
          .fields {
            padding-top: 15px;
            padding-bottom: 15px;
          }
          .fields .label-title {
            display: block;
            font-size: 22px;
            line-height: 24px;
            font-weight: 700;
            margin-top: 10px;
            margin-bottom: 7px;
          }
          .fields .form-control {
            font-size: 18px;
            padding-left: 15px;
          }
          .fields .radio-input-group .label-title {
            margin-bottom: 15px;
            font-size: 22px;
            line-height: 24px;
          }
          .fields .radio-input-group .radio-input-field {
            display: flex;
            align-items: center;
            margin-bottom: 15px;
          }
          .fields .radio-input-group .radio-input-field input {
            margin-right: 10px;
          }
          .fields .radio-input-group .radio-input-field label {
            font-size: 18px;
            line-height: 18px;
            margin-bottom: 0;
            font-weight: 300;
          }
        }
      `}</style>
    </form>
  );
}

export default AskForm;
